import React from "react";
import PlatziConfLogo from "../images/PlatziConfLogo.svg";
import Photo from "../images/IMG_20191129_145043.png";
import MediaLogo from "../images/twitter.jpg"

import "./styles/Badge.css";
import "./styles/BadgeMobile.css"

class Badge extends React.Component {
  render() {
    return (
      <div className="badge">
        <div className="badge-container">
          <div className="badge-header">
            <h3>Platzi Conf</h3>
            <img src={PlatziConfLogo} alt="img" />
          </div>
          <div className="badge-body">
            <img className="profile-picture" src={Photo} alt="img" />
            <p>
              Cristhian <br /> Franco
            </p>
          </div>
          <div className="badge-footer">
            <p className="badge-description">Fullstack Developer</p>
            <div className="badge-social">
              <div className="badge-media-logo"><img src={MediaLogo} alt="img" /></div>              
              <p className="bage">@crodfranco</p>              
            </div>
            <p className="badge-footer-tag">#PlatziConf</p>
          </div>
        </div>
      </div>
    );
  }
}

export default Badge;
