import React from "react";
import "../components/styles/BadgeNew.css";

class BadgeForm extends React.Component {
  state = {
    firstName: '',
    lastName: '',
    email: '',
    jobTitle: '',
    twitter: ''
  }
  
  constructor() {
    super();
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  };

  handleClick = () => {
    console.log("Clicked");
  };

  handleSubmit = (event) => {
    event.preventDefault();
    console.log("Submitted");
    console.log(this.state);
  };

  render() {
    return (
      <div>     
      
        <div className="badgeNew-container">
            <div className="form-container">
            <form onSubmit={this.handleSubmit} className="form-container">
              <h1>New Attendant</h1>
              <label className="form-item">Nombre</label>
              <input
                onChange={this.handleChange}
                className="form-item"
                type="text"
                name="firstName"
                value={this.state.firstName}
              />
              <label className="form-item">Apellido</label>
              <input
                onChange={this.handleChange}
                className="form-item"
                type="text"
                name="lastName"
                value={this.state.lastName}
              />
              <label className="form-item">Email</label>
              <input
                onChange={this.handleChange}
                className="form-item"
                type="email"
                name="email"
                value={this.state.email}
              />
              <label className="form-item">Job Title</label>
              <input
                onChange={this.handleChange}
                className="form-item"
                type="text"
                name="jobTitle"
                value={this.state.jobTitle}
              />
              <label className="form-item">Twitter</label>
              <input
                onChange={this.handleChange}
                className="form-item"
                type="text"
                name="twitter"
                value={this.state.twitter}
              />
              <button
                onClick={this.handleClick}
                className="form-button btn btn-primary"
              >
                Save
              </button>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default BadgeForm;
