import React from 'react';

class BadgesList extends React.Component{
    render(){
        return(
            <ul>
              {this.props.badge.map((badge) => {
                return (
                  <li key={badge.id}>
                    <p>
                      {badge.firstName} {badge.lastName}
                    </p>
                  </li>
                );
              })}
            </ul>
        );
    }
}

export default BadgesList;
